<?php/** * Class Quote_details */class Quote_details{    /**     * attributes of the class     */    /**     * @var int     */    private $idQd;    /**     * @var string     */    private $designation;    /**     * @var string     */    private $unit;    /**     * @var float     */    private $amount;    /**     * @var float     */    private $unitprice;    /**     * @var float     */    private $koefficient;    /**     * @var float     */    private $totaluwt;    /**     * @var int     */    private $quotation_id;    /*================== Getters and Setters =====================*/    /**     * @return int     */    public function getIdQd()    {        return $this->idQd;    }    /**     * @param int $idQd     * @return Quote_details     */    public function setIdQd($idQd)    {        $this->idQd = $idQd;        return $this;    }    /**     * @return string     */    public function getDesignation()    {        return $this->designation;    }    /**     * @param string $designation     * @return Quote_details     */    public function setDesignation($designation)    {        $this->designation = $designation;        return $this;    }    /**     * @return string     */    public function getUnit()    {        return $this->unit;    }    /**     * @param string $unit     * @return Quote_details     */    public function setUnit($unit)    {        $this->unit = $unit;        return $this;    }    /**     * @return float     */    public function getAmount()    {        return $this->amount;    }    /**     * @param float $amount     * @return Quote_details     */    public function setAmount($amount)    {        $this->amount = $amount;        return $this;    }    /**     * @return float     */    public function getUnitprice()    {        return $this->unitprice;    }    /**     * @param float $unitprice     * @return Quote_details     */    public function setUnitprice($unitprice)    {        $this->unitprice = $unitprice;        return $this;    }    /**     * @return float     */    public function getKoefficient()    {        return $this->koefficient;    }    /**     * @param float $koefficient     * @return Quote_details     */    public function setKoefficient($koefficient)    {        $this->koefficient = $koefficient;        return $this;    }    /**     * @return float     */    public function getTotaluwt()    {        return $this->totaluwt;    }    /**     * @param float $totaluwt     * @return Quote_details     */    public function setTotaluwt($totaluwt)    {        $this->totaluwt = $totaluwt;        return $this;    }    /**     * @return int     */    public function getQuotationId()    {        return $this->quotation_id;    }    /**     * @param int $quotation_id     * @return Quote_details     */    public function setQuotationId($quotation_id)    {        $this->quotation_id = $quotation_id;        return $this;    }    /*==========================================================   Creation of CRUD C:insert  R:select U:update D:delete   ==========================================================*/    /**     * Insert function details quote     */    function addQuoteDetails()    {        global $db;        $requete = $db->prepare('INSERT INTO `quote_details`(`designation`, `unit`, `amount`, `unitprice`, `koefficient`, `totaluwt`, `quotation_id`)         VALUES (?,?,?,?,?,?,?)');        $requete->execute(array(            $this->designation,            $this->unit,            $this->amount,            $this->unitprice,            $this->koefficient,            $this->totaluwt,            $this->quotation_id        ));    }    /**     * Display function quote details     * @return false|PDOStatement     */    function getAllQuotedetails($id)    {        global $db;        $quotationListe = $db->prepare('SELECT * FROM quote_details WHERE quotation_id = ? ORDER BY idQd DESC');        $quotationListe->execute(array($id));        return $quotationListe;    }    /**     * Function of recovery data     * @param $id     * @return bool|PDOStatement     */    function recupAllQuotedetails($id)    {        global $db;        $quotationListe = $db->prepare('SELECT designation , unit , amount , unitprice ,         koefficient , totaluwt FROM quote_details WHERE idQd=?');        $quotationListe->execute(array($id));        return $quotationListe;    }    /**     * Delete function all quote details     * @param $id     * @return bool|PDOStatement     */    function DeletAllQuotedetails($id)    {        global $db;        $quotationListe = $db->prepare('DELETE FROM quote_details WHERE quotation_id = ?');        $quotationListe->execute(array($id));        return $quotationListe;    }    /**     *  Delete function quote details     */    function deleteQuotedetails()    {        global $db;        $requete = $db->prepare('DELETE FROM quote_details WHERE idQd = ?');        $requete->execute(array($this->idQd));    }    /**     * Update function quote details     */    function updateQuotedetails()    {        global $db;        $requete = $db->prepare('UPDATE quote_details SET designation = ?, unit = ?,amount = ?,        unitprice = ?,koefficient = ? ,totaluwt = ?  WHERE idQd = ?');        $requete->execute(array(            $this->designation,            $this->unit,            $this->amount,            $this->unitprice,            $this->koefficient,            $this->totaluwt,            $this->idQd        ));    }    /**     * Calculate the sum of a colone function     * @param $id     * @return bool|PDOStatement     */    function calculateSumColone($id)    {        global $db;        $requete = $db->prepare('SELECT SUM(totaluwt) FROM quote_details WHERE quotation_id = ? ;');        $requete->execute(array($id));        return $requete;    }}